import { createSlice } from "@reduxjs/toolkit";
import data from './../data.js'

let items = createSlice({
    name: 'items',
    initialState: data,
    reducer: {
        pushNewItem(state, action) {
            console.log(state, action.payload)
            state.push(action.payload);
        }
    }
});

export default items;
