import { useParams } from "react-router-dom";

function DetailPage(props) {
    let {param} = useParams();
    let item = props.items.find((item)=>{
        if (item.id == param) {
            return true
        }
    })


    // let item = props.items.map(function(item) {
    //     if (item.id == id) {
    //         return item
    //     }
    // });

    return (
        <div className="container">
            <div className="row">
                <div className="col-md-6">
                    <img src={"https://codingapple1.github.io/shop/shoes" + (props.items[id].id + 1) + ".jpg"} width="100%" />
                </div>
                <div className="col-md-6">
                    <h4 className="pt-5">{ props.items[id].title }</h4>
                    <p>{ props.items[id].content }</p>
                    <p>{ props.items[id].price }</p>
                    <button className="btn btn-danger">주문하기</button>
                </div>
            </div>
        </div>
    )
}

export default DetailPage;
