import { createSlice } from "@reduxjs/toolkit";

let carts = createSlice({
    name: 'carts',
    initialState: cartData,
    reducers: {
        addStock(state) {
            state.count += 1;
        },
        subStock(state) {
            state.count -= 1;
        }
    }
});

export default carts;
